var diff_match_patch = require('../');
var fs = require('fs');

var oldText = fs.readFileSync(__dirname + '/t2.txt', 'utf8');
var newText = fs.readFileSync(__dirname + '/quick.js', 'utf8');

var dmp         = new diff_match_patch();
var diff        = dmp.diff_main(oldText, newText, true);
var patchList   = dmp.patch_make(oldText, newText, diff);
var patch       = dmp.patch_toText(patchList);
patch = decodeURI(patch);

console.log("\n", oldText.length, '\n\n', patch);